#include "JoystickDriver.c"
#include "drivebase.c"
#include "OI.c"
#include "latch.c"

task main()
{
	while (true)
	{
		getJoystickSettings(joystick);

		if (OI_is_in_tank_drive(joystick)) //driving
		{
			tankdrive (joystick.joy1_y1, joystick.joy1_y2); //this should be OI_get_right_throttle and the likes, but whe I do that, it throws errors
		}
		if (OI_is_in_translate_drive(joystick))
		{
			translatedrive (joystick.joy1_x1); //end driving
		}

		if (OI_should_open_latch1(joystick)) //latch control
		{
			latch1_open();
		}
		else if (OI_should_close_latch1(joystick))
		{
			latch1_close();
		}
		else if (OI_should_open_latch2(joystick)) //latch control
		{
			latch1_open();
		}
		else if (OI_should_close_latch2(joystick))
		{
			latch1_close();
		}

	}
}
